import { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = () => {
    const [enteredTitle, setEnteredTitle] = useState('');
    const[enteredAmount, setEnteredAmount] = useState('');
    const [enteredDate, setEnteredDate] = useState('');

    // const [enteredValue, setEnteredValue] = useState({
    //     enteredTitle: '',
    //     enteredAmount: '',
    //     enteredDate: ''
    // });

    const titleChangeHandler = (event) =>{
            // setEnteredTitle((prevState) => {
            //     return {...prevState, enteredTitle: event.target.value};
            // });
            setEnteredTitle(event.target.value);
    };

    const amountChangeHandler = (event) =>{
        // setEnteredAmount((prevState) => {
        //     return {...prevState, enteredTitle: event.target.value};
        // });
        setEnteredAmount(event.target.value);
    };

    const dateChangeHandler = (event) => {
        // setEnteredDate((prevState) => {
        //     return {...prevState, enteredTitle: event.target.value};
        // });
        setEnteredDate(event.target.value);
    };

    const addExpenseHandler = (event) => {
        event.preventDefault();
        const ExpenseData = {
            title: enteredTitle,
            amount: enteredAmount,
            date: new Date(enteredDate)
        };

        console.log(ExpenseData);

        setEnteredTitle('');
        setEnteredAmount('');
        setEnteredDate('');

    };

    return(<form onSubmit={addExpenseHandler} >
        <div className='new-expense__controls'>
          <div className='new-expense__date'>
            <label>Title</label>
            <input type='text' onChange={titleChangeHandler} />
          </div>
          <div className='new-expense__amount'>
            <label>Amount</label>
            <input type='number' onChange={amountChangeHandler} min='0' max='100' />
          </div>
          <div className='new-expense__date'>
            <label>Date</label>
            <input type='date' onChange={dateChangeHandler} />
          </div >
        </div>
        <div className='new-expense__actions'>
            <button type='submit'>Add Expense</button>
        </div>
      </form>);

}

export default ExpenseForm;
