import ExpenseItem from './ExpenseItem';
import AddExpense from './AddExpense';
import Card from '../UI/Card';
import './Expense.css'
import ExpensesFilter from './ExpensesFilter';
import {useState} from 'react';
import ExpenseForm from '../NewExpenses/ExpenseForm';


function Expense(props) {
    const [yearSelect, setYearSelect] = useState('2022');

    const selectYearHandler = (year) => {
      setYearSelect(year);
      console.log(year);
    }

    return  (
    <div>
        <div className='expenses'>
        <ExpensesFilter defaultSelectedYear= {yearSelect} onYearSelect={selectYearHandler}/>
        {props.items.map((expense) => (
          <ExpenseForm  title={expense.title} amount={expense.amount} date={expense.date}/>
        ))}
        <Card className='card'>
        <AddExpense />

        <ExpenseItem
        title={props.items[0].title}
        amount={props.items[0].amount}
        date={props.items[0].date}
      />
      <ExpenseItem
        title={props.items[1].title}
        amount={props.items[1].amount}
        date={props.items[1].date}
      />
      <ExpenseItem
        title={props.items[2].title}
        amount={props.items[2].amount}
        date={props.items[2].date}
      />
      <ExpenseItem
        title={props.items[3].title}
        amount={props.items[3].amount}
        date={props.items[3].date}
      />
      </Card>
      </div>
    </div>
        );
}

export default Expense;
